import React from "react";
import ArrowLeft from "../ArrowLeft";
import ArrowUp from "../ArrowUp";
import ArrowRight from "../ArrowRight";
import { Container } from "../Grid";

let duncanIdaho = {
    shields: 600
}

let enemy = {
    shields: 500
}

function BattleMenu () {

    function BattleSystem () {

    }

    function handleClick(e) {
        let enemyShields = 500;
        e.preventDefault();
        console.log("fight");
        let damageDealt = enemyShields/10;
        console.log(damageDealt);
        enemyShields = enemyShields - damageDealt;
        console.log(enemyShields);
    }

    return (
        <div className="alert alert-primary">
            <div className="container">
                <div className="row">
                    <div className="col-sm-3">
                        <button 
                            className="btn btn-warning"
                           onClick={handleClick}
                        >
                        Attack</button>
                        <button className="btn btn-danger">Pulse Attack </button>
                    </div>
                    <div className="col-sm-3">
                        <ul>
                            <li>Sheilds:<p>{duncanIdaho.shields}</p></li>
                            <li>Basic Attack: -10%</li>
                            <li>Pulse Attack: 0%-80%</li>
                        </ul>
                    </div>
                    <div className="col-sm-3">
                        <div className="container">
                            <div className="row">
                                <ArrowUp/>
                            </div>
                            <div className="row">
                                <ArrowLeft/>
                                <ArrowRight/>
                            </div>
                        </div>
                    </div>
                    <div className="col-sm-3">
                        <ul>
                            <li>Sheilds:<p>{enemy.shields}</p></li>
                            <li>Basic Attack:-10%</li>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
}



export default BattleMenu;
