import React from "react";
import ArrowLeft from "../ArrowLeft";
import ArrowUp from "../ArrowUp";
import ArrowRight from "../ArrowRight";
import { Container } from "../Grid";

const enemyShields = 500;

function BattleMenu () {
    function handleClick(e) {
        e.preventDefault();
        console.log("fight");
        let damageDealt = enemyShields/10;
        console.log(damageDealt);
        enemyShields = enemyShields - damageDealt;
        console.log(enemyShields);
    }

    return (
        <div className="alert alert-primary">
            <div className="container">
                <div className="row">
                    <div className="col-sm-3">
                        <button 
                            className="btn btn-warning"

                        >
                        Attack</button>
                        <button className="btn btn-danger">Pulse Attack </button>
                        <button className="btn btn-primary" onClick={handleClick}>Test</button>
                    </div>
                    <div className="col-sm-3">
                        <ul>
                            <li>Sheilds:<p id="playerShields"></p></li>
                            <li>Basic Attack: -10%</li>
                            <li>Pulse Attack: 0%-80%</li>
                        </ul>
                    </div>
                    <div className="col-sm-3">
                        <div className="container">
                            <div className="row">
                                <ArrowUp/>
                            </div>
                            <div className="row">
                                <ArrowLeft/>
                                <ArrowRight/>
                            </div>
                        </div>
                    </div>
                    <div className="col-sm-3">
                        <ul>
                            <li>Sheilds:<p id="enemyShields"></p></li>
                            <li>Basic Attack:-10%</li>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
}



export default BattleMenu;
